*{
    margin: 0;
    padding: 0;
    font-family: "Open Sans", sans-serif;
}
:root{
    --main_color: #173a69;
}


.container{
    width: 1140px;
    margin: 0 auto;
    padding-left: 20px;
    padding-right: 20px;
}
.top_header{
    background: var(--main_color);
    width: 100%;
    height: 35px;
}
.top_header .row{
    display: grid;
    grid-template-columns: 1fr 1fr;
    padding-top: 5px;
}
.top_header .row .flex_column a{
    padding-right: 20px;
    font-size: 15px;
}
.top_header .row .socia_icons{
    text-align: right;
    gap: 20px;
}
.top_header a{
    color: #fff;
    text-decoration: none;
    font-size: 13px;
}
.navber{
    width: 100%;
    height: 100px;
}
.navber .row{
    display: grid;
    grid-template-columns: 1fr 1fr;
    padding-top: 5px;
}

.nav_menu ul {
    display: flex;
    list-style: none;
    justify-content: space-between;
    margin: 0;
    padding: 0;
    align-items: center; 
}

.nav_menu ul li a {
    text-decoration: none;
    color: #000;
    align-items: center; 
    font-size: 18px;
    font-weight: bold;
    line-height: 100px; 
}
